function findGreatestProductForAdjacentNumbers(digitNumber, adjascents) {
    var greatestProduct = 0;
    var candidate = 0;
    var numbers = digitNumber.split('').map(i => parseInt(i));

    for (let i = 0; i < numbers.length; i++) {
        candidate = 0;
        for (let j = 0; j < adjascents; j++) {
            if (!candidate) {
                candidate = numbers[i + j];
            } else {
                candidate*= numbers[i + j];
            }
        }
        if (candidate > greatestProduct) greatestProduct = candidate;
    }

    return greatestProduct;
}



function main() {
    const digitNumber = '';
    const numberOfAdjacents = 13;
    const greatestProduct = findGreatestProductForAdjacentNumbers(digitNumber, numberOfAdjacents);

    console.log(greatestProduct);
}


main();